rule.definition:
load(
   local!product: #"_a-0000d9df-3187-8000-9ba2-011c48011c48_9451"(ri!productSKU),
#"SYSTEM_SYSRULES_formLayout" (
  label: "Restock Product: "&local!product.name,
  firstColumnContents: {
	  #"SYSTEM_SYSRULES_textField"(
	    label: "Product SKU",
	    labelPosition: "ADJACENT",
	    value: ri!productSKU,
	    readOnly: true()
	  ),
	  #"SYSTEM_SYSRULES_textField"(
	    label: "Current Stock",
	    labelPosition: "ADJACENT",
	    value: local!product.quantity,
	    readOnly: true()
	  ),
	  #"SYSTEM_SYSRULES_integerField"(
	    label: "# of New Items",
	    instructions: "Enter the number of new stock items received and being added to the wareshouse",
	    value: ri!RestockCount,
	    saveInto: ri!RestockCount,
	    required: true(),
	    requiredMessage: "You must enter a quantity number",
	    validations: {if(ri!RestockCount<1, "A value greater than 0 must be entered", null)}
	  )
	  
	  },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
          primaryButtons: {
            #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
                label: "Submit",
                style: "PRIMARY",
                confirmMessage:"Are you sure?",
                value: "submit",
                saveInto: ri!button
            )

          },
          secondaryButtons:{
            #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
                label: "Cancel",
                value: "cancel",
                saveInto: {
                  ri!button,
                  a!save(
                    target: ri!RestockCount,
                    value: 0
                  )
                },
                skipValidation: true
            )            
          }
    ) 
)
)
