rule.definition:
=load(
  local!itemsToken,
  {
    #"SYSTEM_SYSRULES_gridLayout"(
      instructions:"Use the semicolon (';') character to separate items in the value cell.",
      headerCells: {
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Label"),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Value"),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "")
      },
      columnConfigs: {
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW")
      },
      rows: #"SYSTEM_SYSRULES_applyComponents"(
        function: #"b13297cb-a557-438e-9c4b-e5c5159917ed"(
          items: ri!labelValue,
          index: _,
          itemsToken: local!itemsToken
        ),
        array: if(or(isnull(ri!labelValue), count(ri!labelValue) < 1), {}, 1+enumerate(count(ri!labelValue))),
        arrayVariable: local!itemsToken
      )
    ),
    #"SYSTEM_SYSRULES_linkField"(
      label: "Add LabelValue Item",
      labelPosition: "COLLAPSED",
      links: #"SYSTEM_SYSRULES_dynamicLink"(
        label: "+Add Item",
        value: 'type!{http://www.appian.com/ae/types/2009}LabelValue'(),
        saveInto: {
          a!save(ri!labelValue, append(ri!labelValue, save!value)),
          /* When modifying the size of the array used in a!applyComponents, /
          / make the same change in the "token" array variable               */
          a!save(local!itemsToken, append(local!itemsToken, save!value))
        }
      )
    )
  }
)
