rule.definition:
=#"SYSTEM_SYSRULES_sectionLayout"(
  label: ri!sectionLabel&" #" & ri!index & " " & index(ri!records,ri!index,"docName",""),
  isCollapsible: true(),
  firstColumnContents:{
    #"SYSTEM_SYSRULES_fileUploadField"(
      label:"Upload Document",
      required:ri!isRequired,
      target:ri!targetFolder,
      documentName: ri!records[ri!index].docName,
      documentDescription: ri!records[ri!index].docDescription,
      value:ri!uploadedDocs[ri!index],
      saveInto: {
        ri!records[ri!index].currentDocId,
        a!save(target:ri!records[ri!index].parentCaseType,value:ri!parentCaseType),
        a!save(target:ri!records[ri!index].parentCaseId,value:ri!parentCaseId),
        a!save(target:ri!records[ri!index].author,value:fn!loggedInUser()),
        a!save(target:ri!records[ri!index].dateCreated,value:now()),
        a!save(target:ri!records[ri!index].isActive,value:true),
        ri!uploadedDocs[ri!index]
      }
    ),
    if(
      count(ri!records) > 1,
      #"SYSTEM_SYSRULES_ButtonLayout"(
        secondaryButtons:
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Remove",
          style: "DESTRUCTIVE",
          value: ri!index,
          saveInto: {
            a!save(ri!records, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2785"(index:save!value, array:ri!records)),
            a!save(ri!uploadedDocs, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2785"(index:save!value, array:ri!uploadedDocs)),
            a!save(ri!recordTokens, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2785"(index:save!value,  array:ri!recordTokens))
          }
        )
      ),
      {}
    ),
    if(count(ri!records) = ri!index,
      #"SYSTEM_SYSRULES_linkField"(
        labelPosition:"COLLAPSED",
        links:#"SYSTEM_SYSRULES_dynamicLink"(
          label:"+Add Another",
          value:1,
          saveInto: {
            a!save(ri!records, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2788"(array:ri!records, index:ri!index)),
            a!save(ri!uploadedDocs, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2788"(array:ri!uploadedDocs, index:ri!index)),
            a!save(ri!recordTokens, #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2788"(array:ri!recordTokens, index:ri!index))
          }
        )
      ),
      {}
    )
  },
  secondColumnContents:
  {
    #"SYSTEM_SYSRULES_textField"(
      label: "Document Name",
      instructions:"If left blank, the original file name will be used.",
      refreshAfter: "KEYPRESS",
      value: index(ri!records,ri!index,"docName",""),
      saveInto: ri!records[ri!index].docName
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "Document Description",
      refreshAfter: "KEYPRESS",
      value: index(ri!records,ri!index,"docDescription",""),
      saveInto: ri!records[ri!index].docDescription
    )
  }
)
